        var impressions = <?php echo json_encode($impressions); ?>;
        var conversions = <?php echo json_encode($conversions); ?>;


        $('#total-impressions').html(impressions.length);
        $('#total-conversions').html(conversions.length);

        var done = {
            impressions : 0,
            conversions : 0
        }

        $('#button-conv').on('click',function(){

            $(this).attr('disabled',true);

            update_stats(conversions[0], 'conversions', 0).done(function () {

                var i = 1;

                setInterval(function () {

                    if (i < conversions.length) {
                        update_stats(conversions[i], 'conversions', i)
                    }
                    else
                        return;

                    i++;
                }, 700);

            });
        });


        $('#button').on('click', function () {

            $(this).attr('disabled',true);

            update_stats(impressions[0], 'impressions', 0).done(function () {

                var i = 1;

                setInterval(function () {

                    if (i < impressions.length) {
                        update_stats(impressions[i], 'impressions', i)
                    }
                    else
                        return;

                    i++;
                }, 700);

            });


        });


        function update_stats(impression, type, i) {

            var dfd = new $.Deferred();

            $.get(ajaxUrl, {
                action: 'update_stats',
                impression: impression,
                id: <?php echo $_GET['id']; ?>,
                i: i,
                type: type
            }).then(function (data) {
                console.log(data);
                done[type]++;
                $('#done-' + type).html(done[type]);
                dfd.resolve(data);
            });

            return dfd.promise();

        }



    </script>

